parameters.functionScript:
if(Inputs.DataExists) {
    localStorage.removeItem('settings');
    Outputs.Done();
}

let facts = Inputs.Facts;
let nodes = [];

// Iterate over all facts
// Make a node for each fact
// If the fact is unlocked, denote this
facts.forEach(fact => {
    let node = {
        'id': fact.index,
        'name': `#${fact.index}`,
        'category': fact.category,
        'unlocked': Inputs.UnlockedFacts.findIndex(uF => uF.index === fact.index) != -1
    };

    nodes.push(node);
});

Outputs.nodes = nodes;
Outputs.Done();

let facts = Inputs.Facts;
let links = [];

// Iterate over all facts
// Make a link from each fact to all other facts that share a tag
facts.forEach(fact => {
    fact.tags.forEach(tag => {
        // Find all facts with the same tag
        let matchingTagFacts = facts.filter((f) => f.tags.findIndex(t => t === tag) != -1 && f != fact);

        matchingTagFacts.forEach(match => {
            let link = {
                'source': fact.index,
                'target': match.index
            };

            links.push(link);
        });

    });
});

// Filter out all links that are the inverse of another link
let filteredLinks = [];
for(let i=0; i < links.length; i++) {
    let link = links[i];
    let linkUnique = links.findIndex(l => l.source === link.target && l.target === link.source) === -1;
    if(linkUnique) {
        filteredLinks.push(link);
    } else {
        // Check if the filtered links has a link like this in it
        let matchingLink = filteredLinks.findIndex(l => l.source === link.source && l.target === link.target) != -1;
        let inverseLink = filteredLinks.findIndex(l => l.source === link.target && l.target === link.source) != -1;
        if(!matchingLink && !inverseLink) {
            filteredLinks.push(link);
        }
    }
}

links = filteredLinks;

Outputs.links = links;
Outputs.Done();

Outputs.data = {
    'nodes': Inputs.nodes,
    'links': Inputs.links
};
Outputs.Done();

let userData = {
    'unlocked-facts': [],
    'highscore': {
        'value': null,
        'date': null
    },
    'last-visit': new Date()
};

localStorage.setItem('userData', JSON.stringify(userData));
Outputs.Done();

function replacePlaceholders(text, answers) {
  let modifiedText = text;

  answers.forEach((answer, index) => {
    const placeholder = `@${index}`;
    if(answer.source === null) {
      modifiedText = modifiedText.replace(placeholder, answer.answer);
    } else {
      modifiedText = modifiedText.replace(placeholder, '<span class="source-note" id="' + index + '">' + answer.answer + '</span>');
    }
  });

  return modifiedText;
}

let content = Inputs.Content;

Outputs.Copy = replacePlaceholders(Inputs.Text, Inputs.Answers);
Outputs.Done();

let answers = Inputs.Answers;
let data = [];

answers.forEach((answer, i) => {
  let source = {
    'Index': i,
    'Copy': answer.source === null ? null : concatURLToDomain(answer.source),
    'Link': answer.source
  };

  if(source.Link != null) {
    data.push(source);
  }
});

function concatURLToDomain(url) {
    let parsedUrl = new URL(url);
    let domain = parsedUrl.hostname;

    // Remove 'www.' if it exists
    if (domain.startsWith('www.')) {
        domain = domain.substring(4);
    }

    return domain;
}

Outputs.Data = data;
Outputs.Count = data.length;
Outputs.Done();

let date = new Date();
let daySeed = date.getFullYear() * 10000 + (date.getMonth() + 1) * 100 + date.getDate();

// Use the seed to determine the random index
let randomIndex = daySeed % Inputs.FactsCount;

Outputs.Index = randomIndex;
Outputs.Done();

function getRandomInt(max) {
  return Math.floor(Math.random() * max);
}

Outputs.Index = getRandomInt(Inputs.FactsCount);
Outputs.Done();

let randomIndex;
if(Inputs.Switch) {
    randomIndex = Inputs.DayRandom;
} else {
    randomIndex = Inputs.TrueRandom;
}

// Get the "random" item
const randomFact = Inputs.Facts[randomIndex];

Outputs.FactID = randomFact.id;
Outputs.Done();

