parameters.functionScript:
Outputs.Text = Inputs.Object.text;
Outputs.Answers = Inputs.Object.answers;
Outputs.Done();

let names = Inputs.Names;
let data = [];
let errors = false;

names.forEach((name) => {
  let nameData = Inputs.MatchingArray.find((n) => n.Name === name);
  if(nameData === undefined) {
    errors = true;
  } else {
    data.push(nameData);
  }
});

Outputs.Data = data;
if(errors) {
  Outputs.Failure();
} else {
  Outputs.Success();
}

if(Inputs.Settings != null) {
    Outputs.DarkMode = Inputs.Settings.darkmode;
    Outputs.StoreData = Inputs.Settings.storeData;
} else {
    Outputs.DarkMode = true;
    Outputs.StoreData = false;
}

Outputs.Done();

if(Inputs.SettingsExists) {
    Outputs.UnlockedFacts = JSON.parse(localStorage.getItem('userData'))['unlocked-facts'];
} else {
    Outputs.UnlockedFacts = [];
}

Outputs.UnlockedFactsCount = Outputs.UnlockedFacts.length;

Outputs.Done();

if(Inputs.CanStoreData) {
    let userData = JSON.parse(localStorage.getItem('userData'));
    userData['last-visit'] = new Date();

    localStorage.setItem('userData', JSON.stringify(userData));
}

Outputs.Done();

let settings = {
    'darkmode': Inputs.DarkMode,
    'storeData': Inputs.StoreData
};

if(Inputs.StoreData) {
    localStorage.setItem('settings', JSON.stringify(settings));
    Outputs.Done();
}

if(Inputs.StoreData) {
    Outputs.Done();
}

function unlockFact(factID, userData) {
    // Check if fact is already unlocked
    if(userData['unlocked-facts'].find((fact) => fact.index === factID) === undefined) {
        // Fact is newly unlocked
        let unlockedFact = {
            'index': factID,
            'date-unlocked': new Date(),
            'quiz': {
                'shown': 0,
                'correct': 0
            }
        }

        userData['unlocked-facts'].push(unlockedFact);
    }
}

if(Inputs.CanStoreData) {
    let userData = JSON.parse(localStorage.getItem("userData"));

    unlockFact(Inputs.FactID, userData);
    localStorage.setItem('userData', JSON.stringify(userData));
    Outputs.Done();
}
