parameters.functionScript:
function replacePlaceholders(id) {
  let modifiedText = Noodl.Objects[id].text;

  Noodl.Objects[id].answers.forEach((answer, index) => {
    const placeholder = `@${index}`;
    modifiedText = modifiedText.replace(placeholder, '<span class="source-note" id="' + index + '">' + answer.answer + '</span>');
  });

  return modifiedText;
}

let content = Inputs.Content;

Outputs.Copy = replacePlaceholders(Inputs.ID);
Outputs.Done();

let answers = Inputs.Answers;
let data = [];

answers.forEach((answer, i) => {
  let source = {
    'Index': i,
    'Copy': concatURLToDomain(answer.source)
  };

  data.push(source);
});

function concatURLToDomain(url) {
    let parsedUrl = new URL(url);
    let domain = parsedUrl.hostname;

    // Remove 'www.' if it exists
    if (domain.startsWith('www.')) {
        domain = domain.substring(4);
    }

    return domain;
}

Outputs.Data = data;
Outputs.Done();

let names = Inputs.Names;
let data = [];
let errors = false;

names.forEach((name) => {
  let nameData = Inputs.MatchingArray.find((n) => n.Name === name);
  if(nameData === undefined) {
    errors = true;
  } else {
    data.push(nameData);
  }
});

Outputs.Data = data;
if(errors) {
  Outputs.Failure();
} else {
  Outputs.Success();
}

